mapper.select:
	select a.*,b.userid,b.phone as login,b.realname from c_zc_order a 
	left join c_customer b on a.cid=b.id
	where 1=1

		and a.cid = #{cid,jdbcType=INTEGER}


		and a.ordercode = #{ordercode,jdbcType=VARCHAR}


		and b.userid = #{userid,jdbcType=INTEGER}


		and b.identity = #{identity,jdbcType=INTEGER}


		and b.parentid = #{parentid,jdbcType=INTEGER}


		and b.phone = #{login,jdbcType=VARCHAR}


		and a.status = #{status,jdbcType=INTEGER}


		and a.type = #{type,jdbcType=INTEGER}


        and a.coin = #{coin,jdbcType=VARCHAR}


	 	and DATE_FORMAT(a.createtime,'%Y%m%d')>=DATE_FORMAT(#{starttime,jdbcType=VARCHAR},'%Y%m%d')


	 	and DATE_FORMAT(a.createtime,'%Y%m%d')<=DATE_FORMAT(#{endtime,jdbcType=VARCHAR},'%Y%m%d')


  		order by a.createtime desc


		order by a.stoptime desc

	select 
		 num,
		ifnull(sum(money),0) as money,
		ifnull(sum(rates),0) as rates,
		ifnull(sum(tax),0) as tax,
	ifnull(sum(balance),0) as balance from (
	    select count(1) as num,
		ifnull(sum(capital),0) as money,
		ifnull(sum(reward),0) as rates,
		ifnull(sum(tax),0) as tax,
		(select ifnull(sum(d.zcbalance),0) from c_wallet d where d.cid=b.id and d.type='USDT') as balance
	from c_customer b
	left join  c_zc_order a on a.cid=b.id
	where 1=1 and identity != 3

		and b.id = #{cid,jdbcType=INTEGER}


		and a.ordercode = #{ordercode,jdbcType=VARCHAR}


		and b.salesman = #{salesman,jdbcType=INTEGER}


		and b.userid = #{userid,jdbcType=INTEGER}


		and b.identity = #{identity,jdbcType=INTEGER}


		and b.parentid = #{parentid,jdbcType=INTEGER}


		and b.phone = #{login,jdbcType=VARCHAR}


		and a.status = #{status,jdbcType=INTEGER}


		and a.type = #{type,jdbcType=INTEGER}


        and a.coin = #{coin,jdbcType=VARCHAR}


	 	and DATE_FORMAT(a.createtime,'%Y%m%d')>=DATE_FORMAT(#{starttime,jdbcType=VARCHAR},'%Y%m%d')


	 	and DATE_FORMAT(a.createtime,'%Y%m%d')<=DATE_FORMAT(#{endtime,jdbcType=VARCHAR},'%Y%m%d')

  	group by b.id ) t
