mapper.select:
 	   select a.*,b.phone as login,b.userid,b.realname,b.parentid,b.salesman from tx_logs a
			left join c_customer b on a.cid=b.id
			where 1=1

				and a.cid = #{cid,jdbcType=INTEGER}


				and b.salesman = #{salesman,jdbcType=INTEGER}


				and b.phone = #{login,jdbcType=VARCHAR}


				and b.userid = #{userid,jdbcType=INTEGER}


				and a.hash = #{hash,jdbcType=VARCHAR}


				and a.tarhash = #{tarhash,jdbcType=VARCHAR}


				and a.toaddr = #{toaddr,jdbcType=VARCHAR}


				and a.status = #{status,jdbcType=INTEGER}


				and
				DATE_FORMAT(a.createtime,'%Y%m%d')>=DATE_FORMAT(#{starttime,jdbcType=VARCHAR},'%Y%m%d')


				and
				DATE_FORMAT(a.createtime,'%Y%m%d')<=DATE_FORMAT(#{endtime,jdbcType=VARCHAR},'%Y%m%d')

		order by a.createtime desc

 	   select count(1) as num,ifnull(sum(valuecoin),0) as valuecoin from tx_logs a
			left join c_customer b on a.cid=b.id
			where 1=1

				and a.cid = #{cid,jdbcType=INTEGER}


				and b.salesman = #{salesman,jdbcType=INTEGER}


				and b.phone = #{login,jdbcType=VARCHAR}


				and b.userid = #{userid,jdbcType=INTEGER}


				and a.hash = #{hash,jdbcType=VARCHAR}


				and a.tarhash = #{tarhash,jdbcType=VARCHAR}


				and a.toaddr = #{toaddr,jdbcType=VARCHAR}


				and a.status = #{status,jdbcType=INTEGER}


				and
				DATE_FORMAT(a.createtime,'%Y%m%d')>=DATE_FORMAT(#{starttime,jdbcType=VARCHAR},'%Y%m%d')


				and
				DATE_FORMAT(a.createtime,'%Y%m%d')<=DATE_FORMAT(#{endtime,jdbcType=VARCHAR},'%Y%m%d')

		order by a.createtime desc
