mapper.update:
update usdt_recharge_log set status=0,tarhash = #{tarhash,jdbcType=VARCHAR}
where  toaddr = #{toaddr,jdbcType=VARCHAR} and status=-1

		and hash = #{hash,jdbcType=VARCHAR}
