mapper.select:
  		select a.*,b.phone as login,b.userid,b.realname,b.parentid,b.salesman from c_cash_usdt_logs a
  		left join c_customer b on a.cid=b.id
  		where 1=1

	        and a.cid = #{cid,jdbcType=INTEGER}


			and b.salesman = #{salesman,jdbcType=INTEGER}


        		and a.hashcode = #{hashcode,jdbcType=VARCHAR}


        		and b.phone = #{login,jdbcType=VARCHAR}


        		and a.toaddr = #{toaddr,jdbcType=VARCHAR}


        		and a.status = #{status,jdbcType=INTEGER}


        		and b.userid = #{userid,jdbcType=INTEGER}


        		and a.dzstatus = #{dzstatus,jdbcType=INTEGER}


		 	and DATE_FORMAT(a.createtime,'%Y%m%d')>=DATE_FORMAT(#{starttime,jdbcType=VARCHAR},'%Y%m%d')


		 	and DATE_FORMAT(a.createtime,'%Y%m%d')<=DATE_FORMAT(#{endtime,jdbcType=VARCHAR},'%Y%m%d')

	  	order by status asc,dzstatus desc,createtime desc

  		select count(1) as num,ifnull(sum(money),0) as money
  		,ifnull(sum(tax),0) as tax ,ifnull(sum(realmoney),0) as realmoney from c_cash_usdt_logs a
  		left join c_customer b on a.cid=b.id
  		where 1=1

	        and a.cid = #{cid,jdbcType=INTEGER}


        		and a.hashcode = #{hashcode,jdbcType=VARCHAR}


        		and b.phone = #{login,jdbcType=VARCHAR}


        		and a.toaddr = #{toaddr,jdbcType=VARCHAR}


        		and a.status = #{status,jdbcType=INTEGER}


        		and b.userid = #{userid,jdbcType=INTEGER}


        		and a.dzstatus = #{dzstatus,jdbcType=INTEGER}


		 	and DATE_FORMAT(a.createtime,'%Y%m%d')>=DATE_FORMAT(#{starttime,jdbcType=VARCHAR},'%Y%m%d')


		 	and DATE_FORMAT(a.createtime,'%Y%m%d')<=DATE_FORMAT(#{endtime,jdbcType=VARCHAR},'%Y%m%d')
